import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class WebScraperService {
  constructor(private http: HttpClient) { }

  scrapeWeb(url: string): Observable<SearchResult[]> {
      return this.http.get<SearchResultObj>(`https://xivapi.com/freecompany/search?name=${url}`)
      //.pipe(map(retVal => retVal.Results))
  };
}


export interface SearchResultObj {
  Results: SearchResult[]
}

export interface SearchResult {
  ID: string;
  Name: string;
  Server: string;
}
